import 'package:flutter/material.dart';
import 'package:project1/main.dart';
import 'package:project1/reuseable_container.dart';
class ListViewScreen extends StatefulWidget {
  const ListViewScreen({super.key});

  @override
  State<ListViewScreen> createState() => _ListScreenState();
}

class _ListScreenState extends State<ListViewScreen> {

   List containername = ["shadab","naseem","babar","rizwan"];
   List containerimage = ["data:image/jpeg;base64,",
   "data:image/jpeg;base64,",
   "data:image/jpeg;base64,",
   "data:image/jpeg;base64,"];

  @override
  Widget build(BuildContext context) {
    return  Scaffold(
      appBar: AppBar(
        backgroundColor: Colors.amberAccent,
        title: const Text("List Screen"),
      ),
      body:
          SingleChildScrollView(
            physics: const ScrollPhysics(),
            child: Column(
              crossAxisAlignment: CrossAxisAlignment.start,
              children: [
                const Center(
                  child: Text("Cricketers", style: TextStyle(
                    fontSize: 50,
fontWeight: FontWeight.w700,


                  ),),
                ),
                ListView.builder(
                  shrinkWrap: true,
                  scrollDirection: Axis.vertical,
                  physics: const ScrollPhysics(),
                  itemCount: containername.length,
                  itemBuilder: ( context, index) {
                    return   reuseablecontainer(
                        ContainerColor: Colors.amberAccent,
                        ContainerText: containername[index],
                        Imageurl: containerimage[index]
                    );
                  },)
              ],
            ),
          )

    );
  }
}

